import React, { useContext } from 'react';
import { NewsContext } from '../NewsContext';
import { Routes, Route, NavLink } from 'react-router-dom';
import NewsArticle from './NewsArticle';
import loaderGif from '../loading.gif';
import Swal from 'sweetalert2';

function modalWindow() {
  Swal.fire({
    title: 'Submit your username',
    input: 'text',
    inputAttributes: {
      autocapitalize: 'off',
    },
    showCancelButton: true,
    confirmButtonText: 'Look up',
    showLoaderOnConfirm: true,
    preConfirm: () => {
      const login = Swal.getPopup().querySelector('#login').value;
      const password = Swal.getPopup().querySelector('#password').value;
      if (!login || !password) {
        Swal.showValidationMessage(`Please enter login and password`);
      }
      return { login: login, password: password };
    },
  }).then((result) => {
    fetch('http://localhost:3000/users')
      .then((response) => response.json())
      .then((users) => {
        const searchIndex = users.findIndex(
          (user) => user.login === result.value.login
        );

        if (searchIndex === -1) {
          Swal.fire({
            icon: 'error',
            title: 'Неверный логин или пароль!',
          });
          return;
        }
        if (users[searchIndex].password === result.value.password) {
          var btn = document.getElementById('header__btn');
          var loginBtn = document.getElementById('login-btn');
          loginBtn.innerHTML = result.value.login;

          localStorage.setItem('user', result.value.login);
          dispatch(setNewUser(result.value.login));
          btn.classList.add('logined');

          Swal.fire({
            icon: 'success',
            title: 'Вы успешно авторизовались!',
          });
        } else
          Swal.fire({
            icon: 'error',
            title: 'Неверный логин или пароль!',
          });
      });
  });
}

function News(props) {
  const { data } = useContext(NewsContext);

  return (
    <div>
      <div className="head__app">
        <h1 className="head__text">NEWS APP</h1>
        <button className="btn" onClick={modalWindow}></button>
        <ul className="list__head">
          <li>
            <NavLink className="bebra" to="/">
              Page one
            </NavLink>
          </li>
          <li>
            <NavLink className="bebra" to="/about">
              Page two
            </NavLink>
          </li>
          <li>
            <NavLink className="bebra" to="/about1">
              Page three
            </NavLink>
          </li>
          <li>
            <NavLink className="bebra" to="/about2">
              Page four
            </NavLink>
          </li>
          <li>
            <NavLink className="bebra" to="/about3">
              Page five
            </NavLink>
          </li>
        </ul>
      </div>
      <div className="all__news">
        {data ? (
          data.articles.map((news) => (
            <NewsArticle data={news} key={news.url} />
          ))
        ) : (
          <div className="loader__box">
            <img className="loader__gif" src={loaderGif} />
          </div>
        )}
      </div>
    </div>
  );
}

export default News;
